import React, { Component } from "react";

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isViewable: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(evt) {
    console.log(this.state.isViewable);
    this.setState({ isViewable: !this.state.isViewable });
  }
  render() {
    return (
      <div>
        {this.state.isViewable ? (
          <div>
            <h1>{this.props.cardInfo.lNum}</h1>
            <button onClick={this.handleClick}>
              <i className="far fa-eye"></i>
            </button>
          </div>
        ) : (
          <div>
            <h1>************{this.props.cardInfo.last4}</h1>{" "}
            <button onClick={this.handleClick}>
              <i className="far fa-eye-slash"></i>
            </button>
          </div>
        )}
        <h1>Exp Date: {this.props.cardInfo.expDate}</h1>
        <h1>CVC: {this.props.cardInfo.cvc}</h1>
      </div>
    );
  }
}

export default Card;
