import React, { Component } from "react";

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isViewable: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(evt) {
    this.setState(isViewable = !this.state.isViewable)
    console.log(this.state.isViewable)
  }
  render() {
    return (
      <div>
        <h1>Card Number: {this.props.cardInfo.lNum}</h1>
        <h1>Exp Date: {this.props.cardInfo.expDate}</h1>
        <h1>CVC: {this.props.cardInfo.cvc}</h1>
        <h1>Last 4: {this.props.cardInfo.last4}</h1>
        <button onClick={this.handleClick}>
          <i className="far fa-eye"></i>
        </button>
      </div>
    );
  }
}

export default Card;
